// when system enabled
document.body.addEventListener('keydown', function (event){
    const keyname = event.key;

    if(keyname == 'h'){
        alert('this is coming soon');
    }
    if(keyname == 'b'){
        alert('this is coming soon');
    }
    if(keyname == 'a'){
        alert('this is coming soon');
    }
    // left keypress
    if(event.keyCode == '37'){
        alert('this is coming soon');
    }
    // right keypress
    if(event.keyCode == '39'){
        alert('this is coming soon');
    }
});