// when system enabled
document.body.addEventListener('keydown', function (event){
    const keyname = event.key;

    if(keyname == 'h'){
        alert('functionality coming soon in V2');
    }
    // up keypress
    else if(keyname == 'w'){
        alert('functionality coming soon in V2');
    }
    // down keypress
    else if(keyname == 's'){
        alert('functionality coming soon in V2');
    }
    // left keypress
    else if(keyname == 'a'){
        alert('functionality coming soon in V2');
    }
    // right keypress
    else if(keyname == 'd'){
        alert('functionality coming soon in V2');
    }

    // scenarios
    else if(keyname == '1'){
        alert('scenario coming soon');
    }
    else if(keyname == '2'){
        alert('scenario coming soon');
    }
    else if(keyname == '3'){
        alert('scenario coming soon');
    }
    else if(keyname == '4'){
        alert('scenario coming soon');
    }
});